# print(n16)
#公钥解密
jm = pow(n16,65537,)
# print(jm)

#转256进制
k = jm
li =[]
while k>0:
    y = k%256
    li.append(y)
    k= k//256
li2 = li[::-1]
# print(li)
# print(li2)

#转为字符串
li3=''
for i in li2:
    li3 += chr(i)
print(li3)

